            {/*            {user.firstname}*/}
            {/*            {user.lastname}*/}
            {/*            {user.email}*/}
            {/*        </li>*/}
            {/*    )}*/}
            {/*</ul>*/}
            {/*<NavigationContainer>*/}
            {/*    <Stack.Navigator>*/}
            {/*        <Stack.Screen name="Home" component={Home} />*/}
            {/*        <Stack.Screen name="Login" component={Login} />*/}
            {/*    </Stack.Navigator>*/}
            {/*</NavigationContainer>*/}
        </View>
    );
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#fff',
        alignItems: 'center',
        justifyContent: 'center',
    },
});

export default App
